package logicalexperession1;

public class Logicalexperession {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 8;

        if (age >= 60 || age <= 12) {
            System.out.println("The person is either a senior citizen or a child.");
        } else {
            System.out.println("The person is neither a senior citizen nor a child.");

	}
	}

}
